r\\u00ecnh logic v\\u1edbi nh\\u1eefng m\\u1ea3nh gh\\u00e9p \\u0111\\u01b0\\u1ee3c s\\u1eafp x\\u1ebfp m\\u1ed9t c\\u00e1ch c\\u00f3 tr\\u1eadt t\\u1ef1 v\\u00e0 khoa h\\u1ecdc. \\\\n\\\\nN\\u01a1i n\\u00e0o c\\u00f3 b\\u1ea1n, n\\u01a1i \\u1ea5y m\\u1ecdi th\\u1ee9 ph\\u1ea3i \\u0111\\u01b0\\u1ee3c x\\u00e2y d\\u1ef1ng tr\\u00ean n\\u1ec1n t\\u1ea3ng v\\u1eefng ch\\u00e3i, th\\u1ef1c t\\u1ebf v\\u00e0 c\\u00f3 c\\u01a1 s\\u1edf. Cho d\\u00f9 l\\u00e0m b\\u1ea5t c\\u1ee9 vi\\u1ec7c g\\u00ec, b\\u1ea1n kh\\u00f4ng ch\\u1ea5p nh\\u1eadn s\\u1ef1 l\\u1ea5p li\\u1ebfm, che \\u0111\\u1eady hay nh\\u1eafm m\\u1eaft l\\u00e0m ng\\u01a1. Nh\\u1eefng \\u0111i\\u1ec1u m\\u00e0 b\\u1ea1n nh\\u00ecn th\\u1ea5y ch\\u1eafc ch\\u1eafn ph\\u1ea3i l\\u00e0 s\\u1ef1 th\\u1eadt. \\\\n\\\\nTr\\u00ean h\\u00e0nh tr\\u00ecnh s\\u1ef1 nghi\\u1ec7p, \\u00f3c th\\u1ef1c ti\\u1ec5n, t\\u00ednh ki\\u00ean tr\\u00ec v\\u00e0 kh\\u1ea3 n\\u0103ng t\\u1ed5 ch\\u1ee9c l\\u00e0 nh\\u1eefng gi\\u00e1 tr\\u1ecb c\\u1ed1t l\\u00f5i m\\u00e0 v\\u0169 tr\\u1ee5 mu\\u1ed1n b\\u1ea1n th\\u1ec3 hi\\u1ec7n. Nh\\u1eefng \\u0111i\\u1ec1u n\\u00e0y s\\u1ebd khi\\u1ebfn b\\u1ea1n ti\\u1ebfp c\\u1eadn v\\u1ea5n \\u0111\\u1ec1 theo c\\u00e1ch ch\\u00ednh th\\u1ed1ng h\\u01a1n l\\u00e0 nh\\u1eefng tr\\u1ea3i nghi\\u1ec7m li\\u1ec1u l\\u0129nh. B\\u00ean c\\u1ea1nh \\u0111\\u00f3, ch\\u00fang t\\u1ea1o cho b\\u1ea1n m\\u1ed9t phong c\\u00e1ch l\\u00e0m vi\\u1ec7c chuy\\u00ean nghi\\u1ec7p trong m\\u1eaft m\\u1ecdi ng\\u01b0\\u1eddi. B\\u1ea1n c\\u00f3 th\\u1ec3 v\\u1ea1ch ra c\\u00e1c m\\u1ee5c ti\\u00eau, k\\u1ebf ho\\u1ea1ch r\\u00f5 r\\u00e0ng, \\u0111\\u1ed3ng th\\u1eddi h\\u1ed7 tr\\u1ee3 ng\\u01b0\\u1eddi kh\\u00e1c b\\u1eb1ng nh\\u1eefng quy tr\\u00ecnh b\\u1ea1n \\u0111\\u00e3 \\u0111\\u00fac k\\u1ebft. \\\\n\\\\nTrong c\\u00f4ng vi\\u1ec7c, t\\u00ednh th\\u1ef1c t\\u1ebf c\\u0169ng s\\u1ebd gi\\u00fap b\\u1ea1n nh\\u00ecn nh\\u1eadn ra nh\\u1eefng v\\u1ea5n \\u0111\\u1ec1 m\\u1ed9t c\\u00e1ch kh\\u00e1ch quan. T\\u1eeb \\u0111\\u00f3, b\\u1ea1n c\\u00f3 th\\u1ec3 s\\u00e1ng su\\u1ed1t \\u0111\\u01b0a ra nh\\u1eefng gi\\u1ea3i ph\\u00e1p h\\u1ee3p l\\u00fd v\\u00e0 t\\u1eebng b\\u01b0\\u1edbc x\\u1eed l\\u00fd n\\u00f3. H\\u00e3y ti\\u1ebfp t\\u1ee5c duy tr\\u00ec th\\u00e1i \\u0111\\u1ed9 l\\u00e0m vi\\u1ec7c nghi\\u00eam t\\u00fac, si\\u00eang n\\u0103ng \\u0111\\u1ec3 \\u0111\\u1ea3m b\\u1ea3o m\\u1ecdi th\\u1ee9 di\\u1ec5n ra \\u0111\\u00fang ti\\u1ebfn \\u0111\\u1ed9. Tuy nhi\\u00ean, khi c\\u1ea3m th\\u1ea5y qu\\u00e1 s\\u1ee9c, t\\u1ed1t h\\u01a1n h\\u1ebft b\\u1ea1n n\\u00ean t\\u00ecm \\u0111\\u1ebfn nh\\u1eefng nhu c\\u1ea7u l\\u00e0nh m\\u1ea1nh \\u0111\\u1ec3 gi\\u1ea3i tho\\u00e1t m\\u00ecnh kh\\u1ecfi nh\\u1eefng c\\u0103ng th\\u1eb3ng. \\\\n\\\\nRung \\u0111\\u1ed9ng tr\\u00ean \\u0111\\u01b0\\u1eddng \\u0111\\u1eddi s\\u1ed1 4 s\\u1ebd khi\\u1ebfn b\\u1ea1n lu\\u00f4n m\\u01b0u c\\u1ea7u v\\u1ec1 s\\u1ef1 an to\\u00e0n v\\u00e0 \\u1ed5n \\u0111\\u1ecbnh cho gia \\u0111\\u00ecnh m\\u00ecnh. Ch\\u00ednh v\\u00ec v\\u1eady, b\\u1ea1n kh\\u00e1 quan t\\u00e2m \\u0111\\u1ebfn vi\\u1ec7c ki\\u1ebfm ti\\u1ec1n v\\u00e0 t\\u00edch l\\u0169y t\\u00e0i ch\\u00ednh. Tuy nhi\\u00ean, b\\u1ea1n ch\\u1ec9 mu\\u1ed1n c\\u00f3 m\\u1ed9t kh\\u1ed1i t\\u00e0i s\\u1ea3n v\\u1eeba \\u0111\\u1ee7 \\u0111\\u1ec3 \\u0111\\u1ea3m b\\u1ea3o t\\u01b0\\u01a1ng lai ch\\u1ee9 kh\\u00f4ng b\\u1ecb \\u00e1m \\u1ea3nh qu\\u00e1 b\\u1edfi nhu c\\u1ea7u ti\\u1ec1n t\\u00e0i. \\\\n\\\\nTrong h\\u00e0nh tr\\u00ecnh c\\u1ee7a m\\u00ecnh, b\\u1ea1n h\\u01b0\\u1edbng t\\u1edbi s\\u1ef1 ch\\u00e2n th\\u00e0nh v\\u00e0 trung th\\u1ef1c, \\u0111\\u1eb7c bi\\u1ec7t l\\u00e0 trong c\\u00e1c m\\u1ed1i quan h\\u1ec7. Gia \\u0111\\u00ecnh v\\u1edbi b\\u1ea1n l\\u00e0 m\\u1ed9t ph\\u1ea7n kh\\u00f4ng th\\u1ec3 thi\\u1ebfu. B\\u1ea1n l\\u1ef1a ch\\u1ecdn b\\u1ea1n b\\u00e8 r\\u1ea5t k\\u1ef9 v\\u00ec kh\\u00f4ng th\\u00edch s\\u1ef1 \\u1ed3n \\u00e0o, n\\u00e1o nhi\\u1ec7t v\\u00e0 c\\u0169ng kh\\u00f4ng mu\\u1ed1n c\\u00f3 nhi\\u1ec1u ng\\u01b0\\u1eddi can d\\u1ef1 v\\u00e0o cu\\u1ed9c s\\u1ed1ng c\\u1ee7a m\\u00ecnh. Qu\\u00e1 th\\u1eb3ng t\\u00ednh, th\\u1eadt th\\u00e0 n\\u00ean \\u0111\\u00f4i khi b\\u1ea1n ch\\u01b0a bi\\u1ebft c\\u00e1ch xoa d\\u1ecbu s\\u1ef1 c\\u0103ng th\\u1eb3ng. B\\u1ea1n th\\u00edch \\u0111\\u01b0\\u1ee3c khen ng\\u1ee3i v\\u00e0 c\\u1ea3m th\\u1ea5y b\\u1ea5t an khi b\\u1ecb ai \\u0111\\u00f3 ph\\u00ea b\\u00ecnh, v\\u00ec s\\u1ef1 c\\u1ea7u to\\u00e0n l\\u00e0 \\u0111i\\u1ec1u b\\u1ea1n lu\\u00f4n mong mu\\u1ed1n \\u0111\\u1ea1t \\u0111\\u01b0\\u1ee3c.&quot;}\\\" data-sheets-userformat=\\\"{&quot;2&quot;:961,&quot;3&quot;:{&quot;1&quot;:0},&quot;9&quot;:0,&quot;10&quot;:1,&quot;11&quot;:4,&quot;12&quot;:0}\\\" style=\\\"\\\"><div style=\\\"text-align: justify;\\\"><font color=\\\"#000000\\\" face=\\\"Arial\\\"><span style=\\\"font-size: 13.3333px;\\\"><br><\\/span><\\/font><\\/div><font color=\\\"#000000\\\" face=\\\"Arial\\\"><div style=\\\"text-align: justify;\\\"><span style=\\\"font-size: 10pt;\\\">B\\u00ean c\\u1ea1nh \\u0111\\u00f3, v\\u0169 tr\\u1ee5 c\\u0169ng mang \\u0111\\u1ebfn cho b\\u1ea1n nhi\\u1ec1u c\\u01a1 h\\u1ed9i \\u0111\\u1ec3 b\\u1ea1n th\\u1ecfa m\\u00e3n v\\u1edbi ni\\u1ec1m \\u0111am m\\u00ea h\\u1ecdc h\\u1ecfi v\\u00f4 h\\u1ea1n. Khi t\\u00ecm hi\\u1ec3u v\\u1ec1 m\\u1ed9t th\\u1ee9 g\\u00ec \\u0111\\u00f3 m\\u1edbi m\\u1ebb, b\\u1ea1n s\\u1eb5n s\\u00e0ng ng\\u1ea5u nghi\\u1ebfn h\\u1ebft m\\u1ecdi ki\\u1ebfn th\\u1ee9c \\u0111\\u1ec3 c\\u00f3 th\\u1ec3 t\\u00ecm th\\u1ea5y ch\\u00e2n l\\u00fd c\\u1ee7a v\\u1ea5n \\u0111\\u1ec1. \\u0110\\u00f3 l\\u00e0 l\\u00fd do b\\u1ea1n d\\u1ec5 d\\u00e0ng tr\\u1edf th\\u00e0nh m\\u1ed9t chuy\\u00ean gia. M\\u1ed9t c\\u00e1ch t\\u1ef1 nhi\\u00ean b\\u1ea1n s\\u1ebd th\\u1ea5y cu\\u1ed9c s\\u1ed1ng c\\u1ee7a m\\u00ecnh gi\\u1ed1ng nh\\u01b0 m\\u1ed9t ph\\u01b0\\u01a1ng tr\\u00ecnh logic v\\u1edbi nh\\u1eefng m\\u1ea3nh gh\\u00e9p \\u0111\\u01b0\\u1ee3c s\\u1eafp x\\u1ebfp m\\u1ed9t c\\u00e1ch c\\u00f3 tr\\u1eadt t\\u1ef1 v\\u00e0 khoa h\\u1ecdc.<\\/span><\\/div><\\/font><div style=\\\"text-align: justify;\\\"><br><\\/div><font color=\\\"#000000\\\" face=\\\"Arial\\\"><div style=\\\"text-align: justify;\\\"><span style=\\\"font-size: 10pt;\\\">N\\u01a1i n\\u00e0o c\\u00f3 b\\u1ea1n, n\\u01a1i \\u1ea5y m\\u1ecdi th\\u1ee9 ph\\u1ea3i \\u0111\\u01b0\\u1ee3c x\\u00e2y d\\u1ef1ng tr\\u00ean n\\u1ec1n t\\u1ea3ng v\\u1eefng ch\\u00e3i, th\\u1ef1c t\\u1ebf v\\u00e0 c\\u00f3 c\\u01a1 s\\u1edf. Cho d\\u00f9 l\\u00e0m b\\u1ea5t c\\u1ee9 vi\\u1ec7c g\\u00ec, b\\u1ea1n kh\\u00f4ng ch\\u1ea5p nh\\u1eadn s\\u1ef1 l\\u1ea5p li\\u1ebfm, che \\u0111\\u1eady hay nh\\u1eafm m\\u1eaft l\\u00e0m ng\\u01a1. Nh\\u1eefng \\u0111i\\u1ec1u m\\u00e0 b\\u1ea1n nh\\u00ecn th\\u1ea5y ch\\u1eafc ch\\u1eafn ph\\u1ea3i l\\u00e0 s\\u1ef1 th\\u1eadt.<\\/span><\\/div><\\/font><div style=\\\"text-align: justify;\\\"><br><\\/div><font color=\\\"#000000\\\" face=\\\"Arial\\\"><div style=\\\"text-align: justify;\\\"><span style=\\\"font-size: 10pt;\\\">Tr\\u00ean h\\u00e0nh tr\\u00ecnh s\\u1ef1 nghi\\u1ec7p, \\u00f3c th\\u1ef1c ti\\u1ec5n, t\\u00ednh ki\\u00ean tr\\u00ec v\\u00e0 kh\\u1ea3 n\\u0103ng t\\u1ed5 ch\\u1ee9c l\\u00e0 nh\\u1eefng gi\\u00e1 tr\\u1ecb c\\u1ed1t l\\u00f5i m\\u00e0 v\\u0169 tr\\u1ee5 mu\\u1ed1n b\\u1ea1n th\\u1ec3 hi\\u1ec7n. Nh\\u1eefng \\u0111i\\u1ec1u n\\u00e0y s\\u1ebd khi\\u1ebfn b\\u1ea1n ti\\u1ebfp c\\u1eadn v\\u1ea5n \\u0111\\u1ec1 theo c\\u00e1ch ch\\u00ednh th\\u1ed1ng h\\u01a1n l\\u00e0 nh\\u1eefng tr\\u1ea3i nghi\\u1ec7m li\\u1ec1u l\\u0129nh. B\\u00ean c\\u1ea1nh \\u0111\\u00f3, ch\\u00fang t\\u1ea1o cho b\\u1ea1n m\\u1ed9t phong c\\u00e1ch l\\u00e0m vi\\u1ec7c chuy\\u00ean nghi\\u1ec7p trong m\\u1eaft m\\u1ecdi ng\\u01b0\\u1eddi. B\\u1ea1n c\\u00f3 th\\u1ec3 v\\u1ea1ch ra c\\u00e1c m\\u1ee5c ti\\u00eau, k\\u1ebf ho\\u1ea1ch r\\u00f5 r\\u00e0ng, \\u0111\\u1ed3ng th\\u1eddi h\\u1ed7 tr\\u1ee3 ng\\u01b0\\u1eddi kh\\u00e1c b\\u1eb1ng nh\\u1eefng quy tr\\u00ecnh b\\u1ea1n \\u0111\\u00e3 \\u0111\\u00fac k\\u1ebft.<\\/span><\\/div><\\/font><\\/span><p><\\/p>\"}'),(184,'CS','Chuyên sâu','CS-DD-5','Số 5 - Chỉ số đường đời',1,'<div style=\"text-align: justify;\"><p style=\"\"><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0); font-family: Arial; text-align: left; white-space: pre-wrap; font-size: 14px;\">Sự nghiệp của bạn sẽ trọn vẹn hơn nếu bạn luôn phát huy trí tưởng tượng không giới hạn của mình. Hãy nhớ rằng, con đường bạn đang đi không khuyến khích bạn để mình chần chừ bởi những lời khuyên từ người khác hoặc phải miễn cưỡng làm theo cách mà họ đang làm. Chính tư duy đột phá của bạn mới tạo ra được những đổi mới trong công việc và điều đó khiến bạn được vinh danh, công nhận.</span></div><span style=\"text-align: left;\"><div style=\"text-align: justify;\"><font color=\"#000000\" face=\"Arial\"><span style=\"white-space: pre-wrap;\"><br></span></font></div><font color=\"#000000\" face=\"Arial\"><span style=\"white-space: pre-wrap;\"><span style=\"font-size: 14px;\">Một cách tự nhiên, bạn sẽ thấy mình có sức hút với nhiều người. Bài học về sự kết nối và truyền cảm hứng cũng là điều vũ trụ muốn bạn lĩnh hội. Hãy dùng khả năng giao tiếp thiên bẩm của mình để gắn kết mọi người, rồi từ đó lan tỏa nguồn năng lượng tích cực và xúc cảm dồi dào của bạn. Với tư duy nghệ thuật cao, mắt thẩm mỹ tốt, và một trực giác nhạy bén, bạn chắc chắn sẽ toả sáng khi được tự do diễn đạt những điều đang ấp ủ.\n\n</span><div style=\"text-align: justify;\"><span style=\"font-size: 14px;\">Với mọi người xung quanh, bạn giàu lòng nhân ái và luôn muốn đem đến điều tốt đẹp nhất cho thế giới. Trên hành trình của mình, khi phải chứng kiến sự bất công, dù không thể tác động trực tiếp nhưng bạn vẫn sẽ cố gắng can thiệp để thay đổi tình huống theo hướng tích cực hơn. Nhờ sức hút của mình, bạn có kha khá các mối quan hệ tình cảm. Lời khuyên nho nhỏ dành cho bạn là hãy luôn trân trọng người bên cạnh thay vì bắt đầu một mối quan hệ mới, bạn sẽ tiếc nuối cho những điều đã qua.</span></div></span></font></span></p></div>','','2021-08-27 14:16:56',NULL,'{\"title\":\"L\\u1eee KH\\u00c1CH\",\"desc\":\"Cu\\u1ed9c \\u0111\\u1eddi s\\u1ebd lu\\u00f4n cho b\\u1ea1n nh\\u1eefng c\\u01a1 h\\u1ed9i b\\u01b0\\u1edbc \\u0111i tr\\u00ean h\\u00e0nh tr\\u00ecnh Kh\\u00e1m Ph\\u00e1, Tr\\u1ea3i Nghi\\u1ec7m v\\u00e0 S\\u00e1ng T\\u1ea1o.\",\"content\":\"<h2 style=\\\"text-align: justify; \\\"><span style=\\\"text-align: start; font-size: 18px;\\\"><b>\\u0110\\u01af\\u1edcNG \\u0110\\u1edcI 5: L\\u1eee KH\\u00c1CH<\\/b><\\/span><\\/h2><div style=\\\"text-align: justify;\\\"><span style=\\\"font-size: 14px;\\\">Cu\\u1ed9c \\u0111\\u1eddi s\\u1ebd lu\\u00f4n cho b\\u1ea1n nh\\u1eefng c\\u01a1 h\\u1ed9i b\\u01b0\\u1edbc \\u0111i tr\\u00ean h\\u00e0nh tr\\u00ecnh Kh\\u00e1m Ph\\u00e1, Tr\\u1ea3i Nghi\\u1ec7m v\\u00e0 S\\u00e1ng T\\u1ea1o.<\\/span><\\/div><div style=\\\"text-align: justify;\\\"><br><\\/div><div style=\\\"text-align: justify;\\\"><span style=\\\"font-size: 14px;\\\">Tr\\u00ean h\\u00e0nh tr\\u00ecnh \\u1ea5y, nh\\u1eefng rung \\u0111\\u1ed9ng v\\u1ec1 s\\u1ef1 khao kh\\u00e1t t\\u1ef1 do, t\\u1eadn h\\u01b0\\u1edfng ni\\u1ec1m vui, chinh ph\\u1ee5c th\\u1eed th\\u00e1ch s\\u1ebd bao tr\\u00f9m l\\u00ean b\\u1ea1n, khi\\u1ebfn b\\u1ea1n c\\u00f3 c\\u1ea3m gi\\u00e1c cu\\u1ed9c s\\u1ed1ng c\\u1ee7a m\\u00ecnh kh\\u00f4ng h\\u1ec1 c\\u00f3 gi\\u1edbi h\\u1ea1n. B\\u1ea1n c\\u0169ng s\\u1ebd c\\u1ea3m nh\\u1eadn \\u0111\\u01b0\\u1ee3c b\\u1ea3n t\\u00ednh ph\\u00f3ng kho\\u00e1ng v\\u00e0 th\\u00edch phi\\u00eau l\\u01b0u c\\u1ee7a m\\u00ecnh. L\\u00fac n\\u00e0o b\\u1ea1n c\\u0169ng quy\\u1ebft li\\u1ec7t l\\u00e0m nh\\u1eefng \\u0111i\\u1ec1u m\\u00ecnh th\\u00edch, cho d\\u00f9 \\u0111\\u00f3 c\\u00f3 th\\u1ec3 l\\u00e0 l\\u1ef1a ch\\u1ecdn sai, th\\u00ec b\\u1ea1n v\\u1eabn th\\u1ea5y n\\u00f3 th\\u1eadt gi\\u00e1 tr\\u1ecb. Ch\\u00ednh nh\\u1eefng tr\\u1ea3i nghi\\u1ec7m th\\u1eadt \\u0111\\u00f3 \\u0111\\u00e3 mang \\u0111\\u1ebfn cho b\\u1ea1n s\\u1ef1 tr\\u01b0\\u1edfng th\\u00e0nh v\\u1edbi t\\u00e0i s\\u1ea3n l\\u00e0 nh\\u1eefng b\\u00e0i h\\u1ecdc s\\u00e2u s\\u1eafc v\\u1ec1 cu\\u1ed9c s\\u1ed1ng.<\\/span><\\/div><div style=\\\"text-align: justify; line-height: 1;\\\"><br><\\/div><p><span data-sheets-value=\\\"{&quot;1&quot;:2,&quot;2&quot;:&quot;\\u0110\\u01af\\u1edcNG \\u0110\\u1edcI 5: L\\u1eee KH\\u00c1CH\\\\nCu\\u1ed9c \\u0111\\u1eddi s\\u1ebd lu\\u00f4n cho b\\u1ea1n nh\\u1eefng c\\u01a1 h\\u1ed9i b\\u01b0\\u1edbc \\u0111i tr\\u00ean h\\u00e0nh tr\\u00ecnh Kh\\u00e1m Ph\\u00e1, Tr\\u1ea3i Nghi\\u1ec7m v\\u00e0 S\\u00e1ng T\\u1ea1o. \\\\n\\\\nTr\\u00ean h\\u00e0nh tr\\u00ecnh \\u1ea5y, nh\\u1eefng rung \\u0111\\u1ed9ng v\\u1ec1 s\\u1ef1 khao kh\\u00e1t t\\u1ef1 do, t\\u1eadn h\\u01b0\\u1edfng ni\\u1ec1m vui, chinh ph\\u1ee5c th\\u1eed th\\u00e1ch s\\u1ebd bao tr\\u00f9m l\\u00ean b\\u1ea1n, khi\\u1ebfn b\\u1ea1n c\\u00f3 c\\u1ea3m gi\\u00e1c cu\\u1ed9c s\\u1ed1ng c\\u1ee7a m\\u00ecnh kh\\u00f4ng h\\u1ec1 c\\u00f3 gi\\u1edbi h\\u1ea1n. B\\u1ea1n c\\u0169ng s\\u1ebd c\\u1ea3m nh\\u1eadn \\u0111\\u01b0\\u1ee3c b\\u1ea3n t\\u00ednh ph\\u00f3ng kho\\u00e1ng v\\u00e0 th\\u00edch phi\\u00eau l\\u01b0u c\\u1ee7a m\\u00ecnh. L\\u00fac n\\u00e0o b\\u1ea1n c\\u0169ng quy\\u1ebft li\\u1ec7t l\\u00e0m nh\\u1eefng \\u0111i\\u1ec1u m\\u00ecnh th\\u00edch, cho d\\u00f9 \\u0111\\u00f3 c\\u00f3 th\\u1ec3 l\\u00e0 l\\u1ef1a ch\\u1ecdn sai, th\\u00ec b\\u1ea1n v\\u1eabn th\\u1ea5y n\\u00f3 th\\u1eadt gi\\u00e1 tr\\u1ecb. Ch\\u00ednh nh\\u1eefng tr\\u1ea3i nghi\\u1ec7m th\\u1eadt \\u0111\\u00f3 \\u0111\\u00e3 mang \\u0111\\u1ebfn cho b\\u1ea1n s\\u1ef1 tr\\u01b0\\u1edfng th\\u00e0nh v\\u1edbi t\\u00e0i s\\u1ea3n l\\u00e0 nh\\u1eefng b\\u00e0i h\\u1ecdc s\\u00e2u s\\u1eafc v\\u1ec1 cu\\u1ed9c s\\u1ed1ng. \\\\n\\\\nTr\\u00ean h\\u00e0nh tr\\u00ecnh s\\u1ef1 nghi\\u1ec7p, b\\u1ea1n s\\u1ebd s\\u1edbm nh\\u1eadn th\\u1ea5y r\\u1eb1ng s\\u1ef1 nh\\u00e0m ch\\u00e1n, \\u0111\\u01a1n \\u0111i\\u1ec7u l\\u00e0 \\u0111i\\u1ec1u b\\u1ea1n kh\\u00f4ng th\\u1ec3 ch\\u1ea5p nh\\u1eadn, cho n\\u00ean b\\u1ea1n lu\\u00f4n ch\\u1ecdn l\\u00e0m nhi\\u1ec1u vi\\u1ec7c c\\u00f9ng l\\u00fac v\\u00e0 c\\u00e0ng h\\u00e1o h\\u1ee9c h\\u01a1n v\\u1edbi nh\\u1eefng tr\\u1ea3i nghi\\u1ec7m m\\u1edbi m\\u1ebb. V\\u1eeba hay, tr\\u1eddi ph\\u00fa cho b\\u1ea1n nhi\\u1ec1u t\\u00e0i n\\u0103ng. V\\u00ec v\\u1eady, trong l\\u0129nh v\\u1ef1c n\\u00e0o b\\u1ea1n c\\u0169ng c\\u00f3 \\u0111\\u1ea5t \\u0111\\u1ec3 th\\u1ec3 hi\\u1ec7n m\\u00ecnh. N\\u1ebfu b\\u1ea1n c\\u00f3 \\u0111\\u1ee7 s\\u1ef1 n\\u1ed7 l\\u1ef1c, k\\u1ef7 lu\\u1eadt v\\u00e0 t\\u1eadp trung, b\\u1ea1n c\\u00f3 th\\u1ec3 th\\u00e0nh c\\u00f4ng \\u1edf b\\u1ea5t k\\u1ef3 c\\u00f4ng vi\\u1ec7c n\\u00e0o m\\u00e0 b\\u1ea1n mu\\u1ed1n. \\\\n\\\\nS\\u1ef1 nghi\\u1ec7p c\\u1ee7a b\\u1ea1n s\\u1ebd tr\\u1ecdn v\\u1eb9n h\\u01a1n n\\u1ebfu b\\u1ea1n lu\\u00f4n ph\\u00e1t huy tr\\u00ed t\\u01b0\\u1edfng t\\u01b0\\u1ee3ng kh\\u00f4ng gi\\u1edbi h\\u1ea1n c\\u1ee7a m\\u00ecnh. H\\u00e3y nh\\u1edb r\\u1eb1ng, con \\u0111\\u01b0\\u1eddng b\\u1ea1n \\u0111ang \\u0111i kh\\u00f4ng khuy\\u1ebfn kh\\u00edch b\\u1ea1n \\u0111\\u1ec3 m\\u00ecnh ch\\u1ea7n ch\\u1eeb b\\u1edfi nh\\u1eefng l\\u1eddi khuy\\u00ean t\\u1eeb ng\\u01b0\\u1eddi kh\\u00e1c ho\\u1eb7c ph\\u1ea3i mi\\u1ec5n c\\u01b0\\u1ee1ng l\\u00e0m theo c\\u00e1ch m\\u00e0 h\\u1ecd \\u0111ang l\\u00e0m. Ch\\u00ednh t\\u01b0 duy \\u0111\\u1ed9t ph\\u00e1 c\\u1ee7a b\\u1ea1n m\\u1edbi t\\u1ea1o ra \\u0111\\u01b0\\u1ee3c nh\\u1eefng \\u0111\\u1ed5i m\\u1edbi trong c\\u00f4ng vi\\u1ec7c v\\u00e0 \\u0111i\\u1ec1u \\u0111\\u00f3 khi\\u1ebfn b\\u1ea1n\\\\n\\u0111\\u01b0\\u1ee3c vinh danh, c\\u00f4ng nh\\u1eadn. \\\\n\\\\nM\\u1ed9t c\\u00e1ch t\\u1ef1 nhi\\u00ean, b\\u1ea1n s\\u1ebd th\\u1ea5y m\\u00ecnh c\\u00f3 s\\u1ee9c h\\u00fat v\\u1edbi nhi\\u1ec1u ng\\u01b0\\u1eddi. B\\u00e0i h\\u1ecdc v\\u1ec1 s\\u1ef1 k\\u1ebft n\\u1ed1i v\\u00e0 truy\\u1ec1n c\\u1ea3m h\\u1ee9ng c\\u0169ng l\\u00e0 \\u0111i\\u1ec1u v\\u0169 tr\\u1ee5 mu\\u1ed1n b\\u1ea1n l\\u0129nh h\\u1ed9i. H\\u00e3y d\\u00f9ng kh\\u1ea3 n\\u0103ng giao ti\\u1ebfp thi\\u00ean b\\u1ea9m c\\u1ee7a m\\u00ecnh \\u0111\\u1ec3 g\\u1eafn k\\u1ebft m\\u1ecdi ng\\u01b0\\u1eddi, r\\u1ed3i t\\u1eeb \\u0111\\u00f3 lan t\\u1ecfa ngu\\u1ed3n n\\u0103ng l\\u01b0\\u1ee3ng t\\u00edch c\\u1ef1c v\\u00e0 x\\u00fac c\\u1ea3m d\\u1ed3i d\\u00e0o c\\u1ee7a b\\u1ea1n. V\\u1edbi t\\u01b0 duy ngh\\u1ec7 thu\\u1eadt cao, m\\u1eaft th\\u1ea9m m\\u1ef9 t\\u1ed1t, v\\u00e0 m\\u1ed9t tr\\u1ef1c gi\\u00e1c nh\\u1ea1y b\\u00e9n, b\\u1ea1n ch\\u1eafc ch\\u1eafn s\\u1ebd to\\u1ea3 s\\u00e1ng khi \\u0111\\u01b0\\u1ee3c t\\u1ef1 do di\\u1ec5n \\u0111\\u1ea1t nh\\u1eefng \\u0111i\\u1ec1u \\u0111ang \\u1ea5p \\u1ee7. \\\\n\\\\nV\\u1edbi m\\u1ecdi ng\\u01b0\\u1eddi xung quanh, b\\u1ea1n gi\\u00e0u l\\u00f2ng nh\\u00e2n \\u00e1i v\\u00e0 lu\\u00f4n mu\\u1ed1n \\u0111em \\u0111\\u1ebfn \\u0111i\\u1ec1u t\\u1ed1t \\u0111\\u1eb9p nh\\u1ea5t cho th\\u1ebf gi\\u1edbi. Tr\\u00ean h\\u00e0nh tr\\u00ecnh c\\u1ee7a m\\u00ecnh, khi ph\\u1ea3i ch\\u1ee9ng ki\\u1ebfn s\\u1ef1 b\\u1ea5t c\\u00f4ng, d\\u00f9 kh\\u00f4ng th\\u1ec3 t\\u00e1c \\u0111\\u1ed9ng tr\\u1ef1c ti\\u1ebfp nh\\u01b0ng b\\u1ea1n v\\u1eabn s\\u1ebd c\\u1ed1 g\\u1eafng can thi\\u1ec7p \\u0111\\u1ec3 thay \\u0111\\u1ed5i t\\u00ecnh hu\\u1ed1ng theo h\\u01b0\\u1edbng t\\u00edch c\\u1ef1c h\\u01a1n. Nh\\u1edd s\\u1ee9c h\\u00fat c\\u1ee7a m\\u00ecnh, b\\u1ea1n c\\u00f3 kha kh\\u00e1 c\\u00e1c m\\u1ed1i quan h\\u1ec7 t\\u00ecnh c\\u1ea3m. L\\u1eddi khuy\\u00ean nho nh\\u1ecf d\\u00e0nh cho b\\u1ea1n l\\u00e0 h\\u00e3y lu\\u00f4n tr\\u00e2n tr\\u1ecdng ng\\u01b0\\u1eddi b\\u00ean c\\u1ea1nh thay v\\u00ec b\\u1eaft \\u0111\\u1ea7u m\\u1ed9t m\\u1ed1i quan h\\u1ec7 m\\u1edbi, b\\u1ea1n s\\u1ebd ti\\u1ebfc nu\\u1ed1i cho nh\\u1eefng \\u0111i\\u1ec1u \\u0111\\u00e3 qua.&quot;}\\\" data-sheets-userformat=\\\"{&quot;2&quot;:961,&quot;3&quot;:{&quot;1&quot;:0},&quot;9&quot;:0,&quot;10&quot;:1,&quot;11&quot;:4,&quot;12&quot;:0}\\\"><\\/span><\\/p><div style=\\\"text-align: justify; box-sizing: border-box;\\\"><span style=\\\"box-sizing: border-box; font-size: 14px;\\\">Tr\\u00ean h\\u00e0nh tr\\u00ecnh s\\u1ef1 nghi\\u1ec7p, b\\u1ea1n s\\u1ebd s\\u1edbm nh\\u1eadn th\\u1ea5y r\\u1eb1ng s\\u1ef1 nh\\u00e0m ch\\u00e1n, \\u0111\\u01a1n \\u0111i\\u1ec7u l\\u00e0 \\u0111i\\u1ec1u b\\u1ea1n kh\\u00f4ng th\\u1ec3 ch\\u1ea5p nh\\u1eadn, cho n\\u00ean b\\u1ea1n lu\\u00f4n ch\\u1ecdn l\\u00e0m nhi\\u1ec1u vi\\u1ec7c c\\u00f9ng l\\u00fac v\\u00e0 c\\u00e0ng h\\u00e1o h\\u1ee9c h\\u01a1n v\\u1edbi nh\\u1eefng tr\\u1ea3i nghi\\u1ec7m m\\u1edbi m\\u1ebb. V\\u1eeba hay, tr\\u1eddi ph\\u00fa cho b\\u1ea1n nhi\\u1ec1u t\\u00e0i n\\u0103ng. V\\u00ec v\\u1eady, trong l\\u0129nh v\\u1ef1c n\\u00e0o b\\u1ea1n c\\u0169ng c\\u00f3 \\u0111\\u1ea5t \\u0111\\u1ec3 th\\u1ec3 hi\\u1ec7n m\\u00ecnh. N\\u1ebfu b\\u1ea1n c\\u00f3 \\u0111\\u1ee7 s\\u1ef1 n\\u1ed7 l\\u1ef1c, k\\u1ef7 lu\\u1eadt v\\u00e0 t\\u1eadp trung, b\\u1ea1n c\\u00f3 th\\u1ec3 th\\u00e0nh c\\u00f4ng \\u1edf b\\u1ea5t k\\u1ef3 c\\u00f4ng vi\\u1ec7c n\\u00e0o m\\u00e0 b\\u1ea1n mu\\u1ed1n.<\\/span><\\/div>\"}'),(185,'CS','Chuyên sâu','CS-DD-6','Số 6 - Chỉ số đường đời',1,'<div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0); font-family: Arial; white-space: pre-wrap;\">Trên hành trình sự nghiệp, hãy sử dụng thường xuyên những thế mạnh về năng lực sáng tạo và khả năng quản lý của mình. Điều này sẽ giúp bạn thể hiện xuất sắc trong các lĩnh vực cần những ý tưởng đột phá hoặc khả năng chăm sóc người khác.</span></div><div style=\"text-align: justify;\"><font color=\"#000000\" face=\"Arial\"><span style=\"white-space: pre-wrap;\"><br></span></font></div><font color=\"#000000\" face=\"Arial\"><div style=\"text-align: justify;\"><span style=\"white-space: pre-wrap;\">Bên cạnh đó, tính tự chủ và tinh thần chịu trách nhiệm tiềm ẩn cũng sẽ định hình rõ nét phong cách làm việc của bạn. Từ trong chính mình bạn luôn tin rằng, nếu bạn muốn hoàn tất việc gì đó, bạn phải tự mình làm lấy. Và rồi với sự quan tâm của mình, bạn sẵn sàng gánh vác luôn công việc của người khác. Hãy học cách lùi lại và tin tưởng vào người khác, họ có thể làm tốt việc của họ, mọi chuyện rồi sẽ ổn.</span></div><div style=\"text-align: justify;\"><span style=\"white-space: pre-wrap;\"><br></span></div><div style=\"white-space: pre-wrap; text-align: justify;\">Vì luôn quan tâm đến đối phương dưới góc nhìn của một bậc cha mẹ, bạn khó lòng làm việc dưới trướng người khác. Do đó, bạn nên phát huy những ưu thế của mình để xây dựng sự nghiệp riêng. Đường đời số 6 sẽ cho bạn nhiều cơ hội để có thể kết nối và tạo dựng nhiều mối quan hệ. Hãy đón nhận những cơ hội đó và đối đãi với mọi người bằng sự chân thành và đáng tin cậy của bạn. Mọi người luôn tin tưởng bạn rằng, mọi bí mật của họ đều được bạn cất giấu kỹ lưỡng.</div><div style=\"text-align: justify;\"><span style=\"white-space: pre-wrap;\"><br></span></div><div style=\"white-space: pre-wrap; text-align: justify;\">Tuy nhiên, trong các mối quan hệ, bạn cần phải học cách cân bằng giữa giúp đỡ và can thiệp. Giàu có là khi bạn có cái để cho đi, và tài sản lớn nhất mà bạn luôn sẵn có là tình yêu thương trong trái tim bạn. Bạn luôn giàu lòng trắc ẩn, vị tha và bao dung, hãy học cách yêu thương đúng, thể hiện lòng trắc ẩn một cách khôn ngoan để sự cho đi của bạn trở nên giá trị.</div></font>','','2021-08-27 14:30:02',NULL,'{\"title\":\"NG\\u01af\\u1edcI M\\u1eb8\",\"desc\":\"Cu\\u1ed9c \\u0111\\u1eddi s\\u1ebd cho b\\u1ea1n nh\\u1eefng c\\u01a1 h\\u1ed9i v\\u1ec1 T\\u00ecnh Y\\u00eau Th\\u01b0\\u01a1ng, t\\u1ea1o d\\u1ef1ng m\\u00e1i \\u1ea5m an l\\u00e0nh \\u0111\\u1ec3 Ch\\u0103m S\\u00f3c v\\u00e0 Che Ch\\u1edf cho nh\\u1eefng ng\\u01b0\\u1eddi b\\u00ean c\\u1ea1nh.\",\"content\":\"<h2 style=\\\"text-align: justify;\\\"><span style=\\\"color: rgb(0, 0, 0); font-family: Arial; font-size: 14px;\\\"><b>\\u0110\\u01af\\u1edcNG \\u0110\\u1edcI 6: NG\\u01af\\u1edcI M\\u1eb8<\\/b><\\/span><\\/h2><span data-sheets-value=\\\"{&quot;1&quot;:2,&quot;2&quot;:&quot;\\u0110\\u01af\\u1edcNG \\u0110\\u1edcI 6: NG\\u01af\\u1edcI M\\u1eb8\\\\r\\\\nCu\\u1ed9c \\u0111\\u1eddi s\\u1ebd lu\\u00f4n cho b\\u1ea1n nh\\u1eefng tr\\u1ea3i nghi\\u1ec7m v\\u00e0 h\\u1ecdc h\\u1ecfi v\\u1ec1 T\\u00ecnh Y\\u00eau Th\\u01b0\\u01a1ng, t\\u1ea1o d\\u1ef1ng m\\u00e1i \\u1ea5m an l\\u00e0nh \\u0111\\u1ec3 Ch\\u0103m S\\u00f3c v\\u00e0 Che Ch\\u1edf cho nh\\u1eefng ng\\u01b0\\u1eddi b\\u00ean c\\u1ea1nh b\\u1ea1n.\\\\r\\\\n\\\\r\\\\nTr\\u00ean h\\u00e0nh tr\\u00ecnh c\\u1ee7a m\\u00ecnh b\\u1ea1n s\\u1ebd c\\u1ea3m nh\\u1eadn \\u0111\\u01b0\\u1ee3c nh\\u1eefng rung \\u0111\\u1ed9ng m\\u1ea1nh m\\u1ebd khi\\u1ebfn b\\u1ea1n lu\\u00f4n mong mu\\u1ed1n th\\u1ec3 hi\\u1ec7n s\\u1ef1 quan t\\u00e2m, ch\\u1ecbu tr\\u00e1ch nhi\\u1ec7m v\\u00e0 b\\u1ea3o v\\u1ec7 \\u0111\\u1ebfn c\\u00f9ng nh\\u1eefng ng\\u01b0\\u1eddi m\\u00ecnh y\\u00eau th\\u01b0\\u01a1ng. B\\u00e0i thi l\\u1edbn m\\u00e0 cu\\u1ed9c \\u0111\\u1eddi \\u0111\\u1eb7t ra cho b\\u1ea1n ch\\u00ednh l\\u00e0 th\\u1ef1c hi\\u1ec7n h\\u00e0nh tr\\u00ecnh c\\u1ee7a m\\u1ed9t b\\u1eadc ph\\u1ee5 huynh m\\u1eabu m\\u1ef1c.\\\\r\\\\n\\\\r\\\\nMang trong m\\u00ecnh nh\\u1eefng t\\u1ed1 ch\\u1ea5t b\\u1ea9m sinh c\\u1ee7a m\\u1ed9t ng\\u01b0\\u1eddi cha, ng\\u01b0\\u1eddi m\\u1eb9, b\\u1ea1n lu\\u00f4n th\\u1ec3 hi\\u1ec7n s\\u1ef1 quan t\\u00e2m, ch\\u0103m s\\u00f3c v\\u00e0 \\u0111\\u1eb7t n\\u1ec1n t\\u1ea3ng gia \\u0111\\u00ecnh l\\u00ean tr\\u00ean h\\u1ebft. V\\u1eady n\\u00ean, kh\\u00f4ng g\\u00ec ng\\u1ea1c nhi\\u00ean khi b\\u01b0\\u1edbc \\u0111i tr\\u00ean con \\u0111\\u01b0\\u1eddng s\\u1ed1 6, b\\u1ea1n lu\\u00f4n ch\\u1ee7 \\u0111\\u1ed9ng h\\u01b0\\u1edbng v\\u1ec1 t\\u1ed5 \\u1ea5m c\\u1ee7a m\\u00ecnh, d\\u00e0nh h\\u1ea7u h\\u1ebft th\\u1eddi gian \\u0111\\u1ec3 vun v\\u00e9n cho n\\u00f3, v\\u00ec v\\u1edbi b\\u1ea1n, gia \\u0111\\u00ecnh l\\u00e0 n\\u01a1i quan tr\\u1ecdng nh\\u1ea5t. Trong h\\u1ec7 gi\\u00e1 tr\\u1ecb c\\u1ee7a b\\u1ea1n, kh\\u00f4ng c\\u00f3 \\u0111i\\u1ec1u g\\u00ec \\u00fd ngh\\u0129a h\\u01a1n m\\u1ed1i quan h\\u1ec7 ho\\u00e0 thu\\u1eadn, g\\u1eafn k\\u1ebft, t\\u00f4n tr\\u1ecdng v\\u00e0 h\\u1ee3p t\\u00e1c c\\u1ee7a c\\u00e1c th\\u00e0nh vi\\u00ean trong gia \\u0111\\u00ecnh. V\\u00ec l\\u1ebd \\u0111\\u00f3, b\\u1ea1n lu\\u00f4n c\\u1ed1 g\\u1eafng lo l\\u1eafng v\\u00e0 th\\u1ea5u hi\\u1ec3u ng\\u01b0\\u1eddi th\\u00e2n c\\u1ee7a m\\u00ecnh m\\u1ed9t c\\u00e1ch tr\\u1ecdn v\\u1eb9n.\\\\r\\\\n\\\\r\\\\nV\\u1edbi \\u0111\\u01b0\\u1eddng \\u0111\\u1eddi s\\u1ed1 6, \\u0111i\\u1ec1u m\\u00e0 v\\u0169 tr\\u1ee5 lu\\u00f4n mong mu\\u1ed1n b\\u1ea1n h\\u1ecdc \\u0111\\u01b0\\u1ee3c l\\u00e0 th\\u1ec3 hi\\u1ec7n t\\u00ecnh y\\u00eau th\\u01b0\\u01a1ng h\\u01a1n l\\u00e0 tr\\u00ecnh di\\u1ec5n nh\\u1eefng k\\u1ef9 n\\u0103ng th\\u00e0nh th\\u1ea1o. T\\u00ecnh y\\u00eau th\\u01b0\\u01a1ng \\u1ea5y kh\\u00f4ng ch\\u1ec9 \\u0111\\u1ec3 d\\u00e0nh cho nh\\u1eefng ng\\u01b0\\u1eddi kh\\u00e1c, m\\u00e0 c\\u00f2n ph\\u1ea3i cho ch\\u00ednh c\\u1ea3 b\\u1ea1n, b\\u1ea1n c\\u0169ng c\\u1ea7n h\\u1ecdc c\\u00e1ch y\\u00eau th\\u01b0\\u01a1ng, v\\u00e0 ch\\u0103m s\\u00f3c m\\u00ecnh th\\u1eadt t\\u1ed1t. \\u0110\\u1eebng qu\\u00e1 lo l\\u1eafng cho ng\\u01b0\\u1eddi kh\\u00e1c m\\u00e0 qu\\u00ean \\u0111i b\\u1ea3n th\\u00e2n m\\u00ecnh. B\\u1ea1n n\\u00ean nh\\u1edb r\\u1eb1ng, khi b\\u1ea1n tr\\u00e2n tr\\u1ecdng m\\u00ecnh, ng\\u01b0\\u1eddi kh\\u00e1c s\\u1ebd tr\\u00e2n tr\\u1ecdng b\\u1ea1n. B\\u00ed quy\\u1ebft \\u0111\\u1ec3 cu\\u1ed9c s\\u1ed1ng c\\u1ee7a b\\u1ea1n tr\\u1edf n\\u00ean t\\u1ed1t \\u0111\\u1eb9p h\\u01a1n \\u0111\\u00f3 l\\u00e0 h\\u00e3y y\\u00eau th\\u01b0\\u01a1ng \\u0111\\u00fang c\\u00e1ch v\\u00e0 lu\\u00f4n nh\\u1eafc nh\\u1edf b\\u1ea3n th\\u00e2n: \\u201cNh\\u1eefng g\\u00ec b\\u1ea1n l\\u00e0m \\u0111\\u00e3 tuy\\u1ec7t l\\u1eafm r\\u1ed3i\\u201d.\