0103ng l\\u01b0\\u1ee3ng c\\u1ee7a ng\\u01b0\\u1eddi c\\u00f3 \\u0110\\u01b0\\u1eddng \\u0110\\u1eddi s\\u1ed1 4:<\\/span><\\/h4><ul style=\\\"text-align: left;\\\"><li>Lu\\u00f4n c\\u00f3 k\\u1ebf ho\\u1ea1ch c\\u1ee5 th\\u1ec3&nbsp;<\\/li><li>Nguy\\u00ean t\\u1eafc v\\u00e0 k\\u1ef7 lu\\u1eadt<\\/li><li>C\\u00f3 tr\\u00e1ch nhi\\u1ec7m cao trong c\\u00f4ng vi\\u1ec7c<\\/li><li>Tr\\u1edf th\\u00e0nh chuy\\u00ean gia trong 1 l\\u0129nh v\\u1ef1c<\\/li><\\/ul><\\/div><div style=\\\"text-align: justify;\\\">B\\u1ea1n lu\\u00f4n l\\u00e0m vi\\u1ec7c <b>ch\\u0103m ch\\u1ec9 v\\u00e0 n\\u1ed7 l\\u1ef1c<\\/b>. B\\u1ea1n <b>tr\\u00e2n tr\\u1ecdng t\\u1eebng \\u0111\\u1ed3ng ti\\u1ec1n<\\/b> m\\u00e0 m\\u00ecnh ki\\u1ebfm \\u0111\\u01b0\\u1ee3c.&nbsp;<\\/div><div style=\\\"text-align: justify;\\\">B\\u1ea1n th\\u1ef1c s\\u1ef1 c\\u1ea3m nh\\u1eadn \\u0111\\u01b0\\u1ee3c s\\u1ef1 kh\\u00f3 kh\\u0103n khi lao \\u0111\\u1ed9ng ki\\u1ebfm ti\\u1ec1n v\\u00e0 <b>lu\\u00f4n \\u0111\\u1ea5u tranh<\\/b> \\u0111\\u1ec3 c\\u00f3 m\\u1ed9t cu\\u1ed9c s\\u1ed1ng t\\u1ed1t \\u0111\\u1eb9p h\\u01a1n. B\\u1ea1n kh\\u00f4ng ph\\u1ea3i ng\\u01b0\\u1eddi ki bo, keo ki\\u1ec7t nh\\u01b0ng b\\u1ea1n <b>bi\\u1ebft chi \\u0111\\u1ed3ng ti\\u1ec1n<\\/b> c\\u1ee7a m\\u00ecnh <b>\\u0111\\u00fang ch\\u1ed7<\\/b>, v\\u00ec th\\u1ebf \\u00edt khi n\\u00e0o b\\u1ea1n l\\u00e2m v\\u00e0o c\\u1ea3nh kh\\u00f3 kh\\u0103n.&nbsp;<\\/div><div style=\\\"text-align: justify;\\\">B\\u1ea1n c\\u0169ng l\\u00e0 ng\\u01b0\\u1eddi<b> coi tr\\u1ecdng v\\u1eadt ch\\u1ea5t, ti\\u1ec1n t\\u00e0i<\\/b>, nh\\u01b0ng kh\\u00f4ng bao gi\\u1edd l\\u00e0m nh\\u1eefng chuy\\u1ec7n tr\\u00e1i v\\u1edbi l\\u01b0\\u01a1ng t\\u00e2m.<\\/div><div style=\\\"text-align: justify;\\\">H\\u00e3y ph\\u00e1t huy nh\\u1eefng \\u0111i\\u1ec3m m\\u1ea1nh \\u0111\\u00f3 v\\u00e0 v\\u1eefng b\\u01b0\\u1edbc tr\\u00ean con \\u0111\\u01b0\\u1eddng c\\u01a1 h\\u1ed9i m\\u00e0 cu\\u1ed9c \\u0111\\u1eddi \\u0111\\u00e3 cho b\\u1ea1n, h\\u00e3y nh\\u00ecn nh\\u1eadn m\\u1ecdi th\\u1ee9 nh\\u01b0 b\\u00e0i h\\u1ecdc m\\u00e0 b\\u1ea1n b\\u1eaft bu\\u1ed9c ph\\u1ea3i t\\u1ed1t nghi\\u1ec7p v\\u00e0 ho\\u00e0n th\\u00e0nh n\\u00f3 m\\u1ed9t c\\u00e1ch xu\\u1ea5t s\\u1eafc nh\\u1ea5t.&nbsp;<\\/div><div style=\\\"text-align: justify;\\\"><br><\\/div><div style=\\\"text-align: justify;\\\"><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 17.55px;\\\"><span style=\\\"font-weight: 600;\\\">Nh\\u1eefng ng\\u01b0\\u1eddi n\\u1ed5i ti\\u1ebfng c\\u00f3 \\u0110\\u01b0\\u1eddng \\u0110\\u1eddi s\\u1ed1 4:<\\/span><\\/span><\\/p><ul style=\\\"text-align: left;\\\"><li><span style=\\\"font-weight: 600;\\\">Donald Trump -<\\/span>&nbsp;C\\u1ef1u T\\u1ed5ng Th\\u1ed1ng M\\u1ef9 - \\\"\\u0110\\u00e3 m\\u1ea5t c\\u00f4ng ngh\\u0129 t\\u1ea1i sao kh\\u00f4ng ngh\\u0129 l\\u1edbn\\\"<\\/li><li><span style=\\\"font-weight: 600;\\\">Bill Gate -&nbsp;<\\/span>\\\"Truy\\u1ec1n h\\u00ecnh kh\\u00f4ng ph\\u1ea3i cu\\u1ed9c s\\u1ed1ng th\\u1ef1c. Trong cu\\u1ed9c s\\u1ed1ng, ng\\u01b0\\u1eddi ta ph\\u1ea3i bi\\u1ebft r\\u1eddi kh\\u1ecfi qu\\u00e1n cafe&nbsp; gi\\u1ea3i tr\\u00ed \\u0111\\u1ec3 \\u0111i l\\u00e0m vi\\u1ec7c\\\"<\\/li><li><span style=\\\"font-weight: 600;\\\">Leonardo da Vinci<\\/span>&nbsp;- \\\"H\\u00e3y h\\u1ecdc c\\u00e1ch nh\\u00ecn. H\\u00e3y nh\\u1eadn ra r\\u1eb1ng m\\u1ecdi th\\u1ee9 \\u0111\\u1ec1u c\\u00f3 k\\u1ebft n\\u1ed1i v\\u1edbi v\\u1ea1n v\\u1eadt\\\"<\\/li><\\/ul><p><span style=\\\"font-weight: 600; font-size: 1.35rem; text-align: left;\\\">G\\u1ee3i \\u00fd v\\u1ec1 c\\u00f4ng vi\\u1ec7c d\\u1ef1a theo ch\\u1ec9 s\\u1ed1 \\u0111\\u01b0\\u1eddng \\u0111\\u1eddi d\\u00e0nh cho b\\u1ea1n:<\\/span><br><\\/p><ul style=\\\"text-align: left;\\\"><li>Nh\\u1eefng c\\u00f4ng vi\\u1ec7c \\u1edf nh\\u1eefng l\\u0129nh v\\u1ef1c \\u0111\\u00f2i h\\u1ecfi k\\u1ef9 n\\u0103ng cao: Qu\\u1ea3n l\\u00fd, chuy\\u00ean gia,&nbsp; b\\u00e1c s\\u0129, ng\\u01b0\\u1eddi hu\\u1ea5n luy\\u1ec7n,...<\\/li><li>Gi\\u1ea3ng vi\\u00ean c\\u00e1c chuy\\u00ean ng\\u00e0nh: m\\u1ef9 thu\\u1eadt, \\u0111i\\u00eau kh\\u1eafc, c\\u00e1c hu\\u1ea5n luy\\u1ec7n vi\\u00ean<\\/li><\\/ul><h6 style=\\\"text-align: left;\\\">=&gt; \\u0110\\u1ec3 bi\\u1ebft \\u0111\\u01b0\\u1ee3c ngh\\u1ec1 nghi\\u1ec7p n\\u00e0o s\\u1ebd khi\\u1ebfn b\\u1ea1n TH\\u0102NG HOA, H\\u1ea0NH PH\\u00daC, h\\u00e3y t\\u00ecm hi\\u1ec3u v\\u1ec1 t\\u1ea5t c\\u1ea3 b\\u1ed9 s\\u1ed1 c\\u1ee7a b\\u1ea1n \\u0111\\u1ec3 TH\\u1ea4U HI\\u1ec2U B\\u1ea2N TH\\u00c2N, PH\\u00c1T HUY \\u0110I\\u1ec2M M\\u1ea0NH v\\u00e0 T\\u00ccM RA&nbsp;NGH\\u1ec0 NGHI\\u1ec6P hay con \\u0111\\u01b0\\u1eddng \\u0111\\u00fang nh\\u1ea5t cho b\\u1ea1n nh\\u00e9!<\\/h6><p style=\\\"text-align: left;\\\"><br><\\/p><table class=\\\"table table-bordered\\\" style=\\\"width: 934.328px; background-color: rgb(255, 255, 255); text-align: left;\\\"><tbody><tr><td><p>Th\\u1ea7n S\\u1ed1 H\\u1ecdc cho m\\u1ed9t ng\\u01b0\\u1eddi l\\u00e0 m\\u1ed9t&nbsp;<span style=\\\"font-weight: 600;\\\">T\\u1eacP S\\u1ed0<\\/span>, g\\u1ed3m c\\u00e1c ch\\u1ec9 s\\u1ed1 kh\\u00e1c nhau, kh\\u00f4ng ch\\u1ec9 c\\u00f3 ch\\u1ec9 s\\u1ed1 \\u0111\\u01b0\\u1eddng \\u0111\\u1eddi, hay s\\u1ee9 m\\u1ec7nh m\\u00e0 c\\u00f2n r\\u1ea5t nhi\\u1ec1u c\\u00e1c ch\\u1ec9 s\\u1ed1 kh\\u00e1c n\\u1eefa. Nh\\u1eefng ch\\u1ec9 s\\u1ed1 ch\\u1ec9 khi t\\u1eadp h\\u1ee3p v\\u1edbi nhau s\\u1ebd ph\\u1ea3n \\u00e1nh&nbsp;<span style=\\\"font-weight: 600;\\\">T\\u00cdNH C\\u00c1CH, SUY NGH\\u0128, N\\u1ed8I T\\u00c2M, NH\\u1eeeNG S\\u1ef0 KI\\u1ec6N, S\\u1ef0 VI\\u1ec6C<\\/span>, s\\u1ebd x\\u1ea3y \\u0111\\u1ebfn trong cu\\u1ed9c \\u0111\\u1eddi m\\u1ed7i ng\\u01b0\\u1eddi<\\/p><p>M\\u1ed9t b\\u1ea3n Th\\u1ea7n S\\u1ed1 H\\u1ecdc ho\\u00e0n ch\\u1ec9nh bao g\\u1ed3m c\\u00e1c con s\\u1ed1 t\\u1eeb 1 \\u0111\\u1ebfn 9 v\\u00e0 m\\u1ed7i con s\\u1ed1 \\u1edf v\\u1ecb tr\\u00ed kh\\u00e1c nhau ph\\u1ea3n \\u00e1nh m\\u1ed9t vai tr\\u00f2 nh\\u1ea5t \\u0111\\u1ecbnh. Trong \\u0111\\u00f3 \\u0111\\u01b0\\u1eddng \\u0111\\u1eddi hay s\\u1ee9 m\\u1ec7nh ch\\u1ec9 l\\u00e0 m\\u1ed9t v\\u1ecb tr\\u00ed m\\u00e0 th\\u00f4i. S\\u1ebd r\\u1ea5t phi\\u1ebfn di\\u1ec5n n\\u1ebfu n\\u00f3i b\\u1ea3n th\\u00e2n l\\u00e0 s\\u1ed1 n\\u00e0y hay s\\u1ed1 kia. Do v\\u1eady, n\\u1ebfu m\\u1ecdi ng\\u01b0\\u1eddi mu\\u1ed1n t\\u00ecm hi\\u1ec3u v\\u1ec1 Th\\u1ea7n S\\u1ed1 H\\u1ecdc m\\u1ed9t c\\u00e1ch&nbsp;<span style=\\\"font-weight: 600;\\\">C\\u1ee4 TH\\u1ec2&nbsp;<\\/span>v\\u00e0&nbsp;<span style=\\\"font-weight: 600;\\\">CH\\u00cdNH X\\u00c1C<\\/span><\\/p><p><span style=\\\"font-weight: 600;\\\">H\\u00c3Y \\u0110\\u0102NG K\\u00cd TRA C\\u1ee8U CHUY\\u00caN S\\u00c2U \\u0110\\u1ec2 T\\u00ccM HI\\u1ec2U V\\u1ec0 \\\"T\\u1eacP S\\u1ed0\\\" \\u0110\\u1ea6Y \\u0110\\u1ee6 C\\u1ee6A B\\u1ea0N<\\/span><br><\\/p><\\/td><\\/tr><\\/tbody><\\/table><\\/div>\",\"SU_MENH\":\"<h1>S\\u1ee9 m\\u1ec7nh s\\u1ed1 4:<\\/h1><p>B\\u1ea1n s\\u1ebd tr\\u1edf th\\u00e0nh m\\u1ed9t \\\"<b>B\\u1eacC TH\\u1ea6Y X\\u00c2Y D\\u1ef0NG\\\"<\\/b>&nbsp;trong cu\\u1ed9c s\\u1ed1ng n\\u00e0y, v\\u00ec n\\u00f3 \\u0111\\u00e3 t\\u1ed3n t\\u1ea1i b\\u00ean trong b\\u1ea1n.<\\/p><h3><span style=\\\"font-size: 1.5rem;\\\">T\\u01b0\\u1ee3ng tr\\u01b0ng:<\\/span><br><\\/h3><ul><li>C\\u00f4ng b\\u00ecnh, tr\\u00e1ch nhi\\u1ec7m v\\u00e0 b\\u00ecnh y\\u00ean<\\/li><li>B\\u1ec1n b\\u1ec9, t\\u00edn c\\u1ea9n, tr\\u1ea7m l\\u1eb7ng, th\\u1eb3ng th\\u1eafn<\\/li><\\/ul><p>B\\u1ea1n lu\\u00f4n <b>L\\u00c0M VI\\u1ec6C CH\\u0102M CH\\u1ec8<\\/b>, b\\u1ea1n <b>CAM K\\u1ebeT V\\u1edaI M\\u1ee4C TI\\u00caU&nbsp;<\\/b>c\\u1ee7a b\\u1ea1n v\\u00e0 mu\\u1ed1n th\\u1ec3 hi\\u1ec7n ch\\u1ea5t l\\u01b0\\u1ee3ng t\\u1ed1t nh\\u1ea5t m\\u1ed9t khi b\\u1ea1n \\u0111\\u00e3 quy\\u1ebft \\u0111\\u1ecbnh l\\u00e0m. B\\u1ea1n l\\u00e0 n\\u1ec1n t\\u1ea3ng c\\u1ee7a b\\u1ea5t k\\u1ef3 doanh nghi\\u1ec7p n\\u00e0o, m\\u1ed9t h\\u1ea1t gi\\u1ed1ng kh\\u00f4ng th\\u1ec3 thi\\u1ebfu \\u0111\\u1ec3 h\\u00ecnh th\\u00e0nh n\\u00e